mapper.insert:

  		INSERT 
  			INTO 
  		COMPANY_RECRUIT (
		      COMPANY_RECRUIT_IDX
		    , COMPANY_ID
		    , COMPANY_NAME
		    , RECRUIT_TITLE
		    , COMPANY_JOB
		    , GETMAN
		    , REGION_IDX
		    , COMPANY_ADDRESS
		    , COMPANY_INFO
		    , APPLICATION_DEADLINE
		    , COMPANY_ESTABLISH

				) VALUES (
				      COMPANY_RECRUIT_SEQ.NEXTVAL
				    , #{company_id}
				    , #{company_name}
				    , #{recruit_title}
				    , #{company_job}
				    , #{getman}
				    , #{region_idx}
				    , #{company_address}
				    , #{company_info}
				    , #{application_deadline}
				    , #{company_establish}
				    )

  		INSERT 
  			INTO 
  		COMPANY_RECRUIT (
		    COMPANY_RECRUIT_IDX,
		    COMPANY_ID,
		    COMPANY_NAME,
		    RECRUIT_TITLE,
		    COMPANY_JOB,
		    GETMAN,
		    REGION_IDX,
		    COMPANY_ADDRESS,
		    COMPANY_INFO,
		    APPLICATION_DEADLINE,
		    COMPANY_ESTABLISH,
		    COMPANY_RECRUIT_REGDATE,
    		VIEWS
				) VALUES (
				      COMPANY_RECRUIT_SEQ.NEXTVAL,
				      #{company_id}
				    , #{company_name}
				    , #{recruit_title}
				    , #{company_job}
				    , #{getman}
				    , #{region_idx}
				    , #{company_address}
				    , #{company_info}
				    , #{application_deadline}
				    , #{company_establish}
				    , #{company_recruit_regdate}
		    		, #{views}
				)

mapper.select:
 	SELECT COUNT( * )
 	FROM  company_recruit
	    WHERE company_id = #{ company_id }

SELECT *
FROM company_recruit
WHERE company_recruit_idx = #{ company_recruit_idx}

mapper.update:
    UPDATE COMPANY_RECRUIT
		SET
			  RECRUIT_TITLE = #{recruit_title}
			, COMPANY_NAME = #{company_name}  
		    , COMPANY_JOB = #{company_job}
		    , GETMAN = #{getman}
		    , REGION_IDX = #{region_idx}
		    , COMPANY_ADDRESS = #{company_address}
		    , COMPANY_INFO = #{company_info}
		    , APPLICATION_DEADLINE = #{application_deadline}
		    , COMPANY_ESTABLISH = TO_TIMESTAMP(#{company_establish}, 'YYYY-MM-DD HH24:MI:SS.FF')
		WHERE
		     COMPANY_RECRUIT_IDX = #{company_recruit_idx}
