mapper.insert:
INSERT INTO COMPANY_USERS (
     COMPANY_ID
   , COMPANY_PASSWD
   , COMPANY_USER_NAME
   , COMPANY_PHONE
   , COMPANY_NAME
   , COMPANY_BCODE
   , COMPANY_BOSS_NAME
   , COMPANY_EMAIL
   , COMPANY_USER_EMAIL
   , COMPANY_REGDATE
   , COMPANY_ESTABLISH

) VALUES (
      #{ company_id }
    , #{ company_passwd }
    , #{ company_user_name }
    , #{ company_phone }
    , #{ company_name }
    , #{ company_bcode }
    , #{ company_boss_name }
    , #{ company_email }
    , #{ company_user_email }
    , SYSDATE 
    , #{ company_establish }
)

mapper.update:
UPDATE COMPANY_USERS
SET	   COMPANY_PASSWD = #{ company_passwd }
	   , COMPANY_USER_NAME = #{ company_user_name }
	   , COMPANY_PHONE = #{ company_phone }
	   , COMPANY_NAME = #{ company_name }
	   , COMPANY_BCODE = #{ company_bcode }
	   , COMPANY_BOSS_NAME = #{ company_boss_name }
	   , COMPANY_EMAIL = #{ company_email }
	   , COMPANY_ESTABLISH = #{ company_establish }
WHERE 
	COMPANY_ID = #{ company_id }
