mapper.insert:
INSERT INTO 
 USER_RESUME (
    USER_RESUME_IDX
  , USER_TITLE
  , USER_ID
  , USER_NAME
  , USER_BIRTH
  , USER_PHONE
  , USER_EMAIL
  , REGION_IDX
  , REGION_ADDRESS
  , USER_INTRO
) VALUES (
    USER_RESUME_SEQ.NEXTVAL
  , #{ user_title }
  , #{ user_id }
  , #{ user_name }
  , #{ user_birth }
  , #{ user_phone }
  , #{ user_email }
  , #{ region_idx }
  , #{ region_address }
  , #{ user_intro }
)

insert.foreach:
INTO COMMON_USER_RESUME_SKILL 
VALUES (
    GET_FILENUM()
    , #{ skill.skill_name }
    , #{ user_resume_idx }
)

mapper.update:
UPDATE 
	USER_RESUME
SET
	USER_TITLE = #{ user_title }
	, USER_BIRTH = #{ user_birth }
	, USER_EMAIL = #{ user_email }
	, REGION_IDX = #{ region_idx }
	, REGION_ADDRESS = #{ region_address }
	, USER_INTRO = #{ user_intro }
WHERE
	USER_RESUME_IDX = #{ user_resume_idx }
